<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$respuesta = array();			$consulta = "SELECT *  FROM ventas 			WHERE estatus_ventas = 'PENDIENTE'";		$result = mysqli_query( $link, $consulta ) 	or die("Error al ejecutar consulta: $consulta".mysqli_error($link));				while($venta = mysqli_fetch_assoc($result)) {		$consulta_productos = "		SELECT * FROM  ventas_detalle		LEFT JOIN productos USING(id_productos)		WHERE id_ventas = '{$venta["id_ventas"]}'";				$result_productos = mysqli_query( $link, $consulta_productos );		if($result_productos){			$respuesta["consulta_productos"][] = $consulta_productos; 						while($productos = mysqli_fetch_assoc($result_productos)) {				$respuesta["productos"][] = $productos; 								$venta["productos"][] = $productos; 											}		}		else{			$respuesta["result_productos"] = mysqli_error($link); 		}				$respuesta["ventas"][] =  $venta;			}		$respuesta["num_filas"] = mysqli_num_rows($result) ;			$respuesta["mensaje"] =  $count_rows < 1 ? $mensaje_error : "OK";	$respuesta["encontrado"] =  $count_rows < 1 ? 0 : 1;	$respuesta["consulta"] =  $consulta;			print(json_encode($respuesta));		?>	